import React from 'react';
import './home.css';
import Projects from './Projects';
import TimeLine from './TimeLine';
import FontAwesome from 'react-fontawesome';
import 'font-awesome/css/font-awesome.min.css';
const Home=()=>{
    return (
        <>
<div className="bgattachedImage">
<div className="text-white text-center rgba-stylish-strong Home_banner__fFRb6">
    <h5 className="h5 primary-colour">
        <FontAwesome className="fas fa-briefcase mr-1"/>
            Portfolio</h5>

            <div className="TextAnimationh1 mt-5">
      <h1>
   <span
     className="txt-rotate helements"
     data-period="2000"
     data-rotate='[ "I am Developer"," I am Blogger" ]'
   ></span>
 </h1>
 </div>
 <div className="col-6 mx-auto mt-3">
 <p><FontAwesome className="fas fa-quote-left"/>When you change the quality of your thinking, you change the quality of your life, sometimes instantly. Just as positive words can make someone smile or a well-timed humorous quote can make someone laugh, our thoughts react to the world in real-time. <FontAwesome className="fas fa-quote-right"/></p>
                     <a target="_blank" className=" resumebtn primary-bgColour waves-effect waves-light" href="#"><FontAwesome className="fas fa-clone left mr-1"/>
                             View Resume</a>
</div></div>
</div>
 <div className="Home_body__2gksT col-12 ">
      <div className="row">
